import auth from '@/assets/auth';
import 'vue-good-table/dist/vue-good-table.css';
import { VueGoodTable } from 'vue-good-table';
export default {
  name: 'SetUp',
  components: {
    VueGoodTable
  },
  data() {
    return {
      registrationsList: [],
      columns: [{
        label: 'Name',
        field: this.concatName
      }, {
        label: 'Email',
        field: 'email'
      }, {
        label: 'Paid',
        field: this.paidAmount
      }]
    };
  },
  methods: {
    async grabRegistrations() {
      const url = auth.allRegistrations;
      const headers = {
        credentials: 'same-origin',
        'Content-Type': 'application/json',
        'X-WP-Nonce': this.nonce
      };
      fetch(url, {
        method: 'GET',
        headers
      }).then(result => result.json()).then(result => {
        this.registrationsList = result;
      });
    },
    concatName(rowObj) {
      // eslint-disable-next-line
      return rowObj.name + ' ' + rowObj.surname;
    },
    paidAmount(rowObj) {
      return 'CHF ' + rowObj.paid;
    }
  },
  mounted() {
    this.grabRegistrations();
  }
};
