const rootUrl = document.location.origin;
const config = {
  rootUrl,
  optionsRoute: `${rootUrl}/wp-json/core-vue/options`,
  headers: {
    credentials: 'same-origin',
    'Content-Type': 'application/json',
  },
};
export default config;
